  `so_source_ids` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '对阵id, 多个用逗号隔开，头尾加逗号用于精确匹配  ,1,2,3,',
  `so_source_ids_from` tinyint(2) NOT NULL COMMENT '1来源schedules表, 2来源outright表',
