  `so_st_id` int(10) UNSIGNED NOT NULL DEFAULT 0 COMMENT '球类id',
  `so_rebate_ratio` float NOT NULL DEFAULT 0 COMMENT '返回比例',
  `so_rebate_amount` decimal(12, 3) NOT NULL DEFAULT 0.000 COMMENT '返水金额',
  `so_bet_amount` decimal(12, 3) NOT NULL DEFAULT 0.000 COMMENT '下注金额',
  `so_bet_info` text CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '注单内容：盘口id, 玩法名称, 玩法的赔率key值, 赔率.....',
  `so_event_type` tinyint(2) NOT NULL DEFAULT 0 COMMENT ' 1 滚球，2 今日赛事，3早盘，4综合过关',
  `so_check_status` tinyint(2) NOT NULL DEFAULT 0 COMMENT '审核状态 0未审核， 1审核通过，2系统自动审核不通过，3人工审核不通过',
  `so_bet_status` tinyint(2) NOT NULL DEFAULT 0 COMMENT '默认0，1 赢，2 赢一半，3 输，4 输一半，5 和局本金返还',
  `so_to_win` decimal(12, 3) NOT NULL DEFAULT 0.000 COMMENT '预计最大可赢金额（不包括本金）',
  `so_bonus` decimal(12, 3) NOT NULL DEFAULT 0.000 COMMENT '结算后的可派奖金额（包括本金）',
  `so_bonus_no_principal` decimal(12, 3) NOT NULL DEFAULT 0.000 COMMENT '实际输赢，不带本金的奖金',
  `so_status` tinyint(2) NOT NULL DEFAULT 1 COMMENT '1 等待开奖， 2等待人工开奖，3 审核不通过等待撤票，4 赛果异常等待撤票， 5 已结算，6 已派奖， 7 系统撤票，8 人工撤票，9 撤单失败',
  `so_remark` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '订单备注，如危险球的 ‘进球无效’，‘红牌无效’等',
  `so_ip` varchar(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL DEFAULT '' COMMENT '下注ip',
  `so_distribute_time` timestamp(0) NOT NULL COMMENT '派奖时间',
  `so_calculate_time` timestamp(0) NOT NULL COMMENT '算奖时间',
  `so_create_time` timestamp(0) NOT NULL COMMENT '下注时间',
  `so_modify_time` timestamp(0) NOT NULL,
  PRIMARY KEY (`so_id`) USING BTREE,
  INDEX `so_check_status`(`so_st_id`, `so_source_ids_from`, `so_status`, `so_check_status`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 1 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Compact;

