DROP TRIGGER IF EXISTS `insert_activity_trigger`;
delimiter ;;
CREATE DEFINER = `dscp`@`%` TRIGGER `insert_activity_trigger` AFTER INSERT ON `ds_activity` FOR EACH ROW BEGIN
		*
	FROM
		`ds_activity`
	WHERE
		activity_id = new.activity_id;
END
;;
delimiter ;

-- ----------------------------
-- Triggers structure for table ds_activity
-- ----------------------------
DROP TRIGGER IF EXISTS `update_activity_trigger`;
delimiter ;;
CREATE DEFINER = `dscp`@`%` TRIGGER `update_activity_trigger` AFTER UPDATE ON `ds_activity` FOR EACH ROW BEGIN
       DELETE from try_dscp.`ds_activity`  where try_dscp.`ds_activity`.activity_id=new.activity_id;
		*
	FROM
		`ds_activity`
              WHERE
       activity_id = new.activity_id;
END
;;
delimiter ;

